INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('01ad8998268101ad186babf43dac30a4', '01ad8998268101ad186babf43dac30a4', 'de', 'In den Standard-Vertretungseinstellungen können Dozierende eine Standard-Vertretung festlegen, die alle Veranstaltungen des Dozierenden verwalten und ändern kann.', 'dispatch.php/settings/deputies', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('0237ea35a203be81e44c979d82ef5ee6', '0237ea35a203be81e44c979d82ef5ee6', 'en', 'Archived courses to which the user is allocated appear here. Contents can no longer be changed, however deposited documents can be downloaded as a zip file.', 'dispatch.php/my_courses/archive', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('02b4e3ce7b8fe6b3e6a3586d410a51a1', '02b4e3ce7b8fe6b3e6a3586d410a51a1', 'en', 'This page displays the study groups to which the user is allocated. Study groups are a simple possibility to cooperate with fellow students, colleagues and others. Each user can create study groups or search for them. The colour grouping can be adjusted individually.', 'dispatch.php/my_studygroups', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('04457f9a66eab07618fe502d470a9711', '04457f9a66eab07618fe502d470a9711', 'de', 'In der Übersicht finden sich veranstaltungsbezogene Kurz- und Detail-Informationen, Ankündigungen, Termine und Umfragen.', 'dispatch.php/course/overview', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('0838a96b5678e2fc26be0ee38ae67619', '0838a96b5678e2fc26be0ee38ae67619', 'en', 'In DoIT! lecturers have the possibility to set various types of tasks, including the uploading of files, Multiple-Choice questions and Peer Reviewing. The processing of tasks can be time limited and alternatively carried out in groups.', 'plugins.php/reloadedplugin/show', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('0ad754cc62d1e86e97c1a28dd68ac40c', '0ad754cc62d1e86e97c1a28dd68ac40c', 'en', 'An overview on the dates, which were booked by students can be found here.', 'plugins.php/homepageterminvergabeplugin/show_bookings', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('0c055cc6ae418a96ff3afa9db13098df', '0c055cc6ae418a96ff3afa9db13098df', 'en', 'The properties of the course can be subsequently changed with the management functions. The simulation of the students view is possible under actions.', 'dispatch.php/course/management', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('0d83ce036f2870f873446230c0118bb7', '0d83ce036f2870f873446230c0118bb7', 'en', 'The learning module interface makes it possible for self-learning units or tests to be made available from external programmes such as ILIAS and LON-CAPA in Stud.IP.', 'dispatch.php/course/elearning/show', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('0e816d9428a3bc8a73fb0042fb2da540', '0e816d9428a3bc8a73fb0042fb2da540', 'en', 'The affiliation to user domains can be viewed, however not changed, here.', 'dispatch.php/settings/userdomains', '3.1', 0, 0, 1, '', '', 1412942388, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('1058f03da5b6fc6a5ff3a08c9c1fa5f7', '1058f03da5b6fc6a5ff3a08c9c1fa5f7', 'de', 'Hier können der Veranstaltung weitere Funktionen hinzugefügt werden.', 'dispatch.php/course/plus', '3.1', 0, 0, 1, '', '', 1406641688, 0);
INSERT INTO `help_content` (`global_content_id`, `content_id`, `language`, `content`, `route`, `studip_version`, `position`, `custom`, `visible`, `author_email`, `installation_id`, `mkdate`, `chdate`) VALUES('1289e991a93dce5a0b4edd678514325e', '1289e991a93dce5a0b4edd678514325e', 'de', 'Hier können einzelne Inhaltselemente nachträglich aktiviert oder deaktiviert werden. Aktivierte Inhaltselemente fügen neue Funktionen zu Ihrem Profil oder Ihren Einstellungen hinzu. Diese werden meist als neuer Reiter im Menü erscheinen. Wenn Funktionalitäten nicht benötigt werden, können diese hier deaktiviert werden. Die entsprechenden Menüpunkte werden dann ausgeblendet.', 'dispatch.php/profilemodules', '3.1', 0, 0, 1, '', '', 1406641688, 0);
