INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('edfcf78c614869724f93488c4ed09582', 7, 'Gruppe benennen', 'Sie können in den Vorlagen nach einem passenden Gruppennamen suchen und ihn mit dem gelben Doppelpfeil auswählen. Alternativ haben Sie auch die Möglichkeit, einen neuen Gruppennamen zu bestimmen, indem Sie im rechten Feld den Namen direkt eintragen.', 'B', 0, 'SELECT', 'admin_statusgruppe.php', '', '', '', 1405689541, 0);
INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('edfcf78c614869724f93488c4ed09582', 8, 'Gruppengröße', 'Mit dem Feld \"Gruppengröße\" können Sie die maximale Anzahl der Teilnehmer einer Gruppe festlegen. Wenn Sie dies nicht benötigen, lassen Sie das Feld einfach leer.', 'B', 0, 'INPUT#role_size', 'admin_statusgruppe.php', '', '', '', 1405689763, 0);
INSERT INTO `help_tour_steps` (`tour_id`, `step`, `title`, `tip`, `orientation`, `interactive`, `css_selector`, `route`, `action_prev`, `action_next`, `author_email`, `mkdate`, `chdate`) VALUES('edfcf78c614869724f93488c4ed09582', 9, 'Selbsteintrag', 'Wenn Sie die Funktion \"Selbsteintrag\" aktivieren, können sich die Teilnehmenden der Veranstaltung selbst in die Gruppen eintragen.', 'B', 0, 'INPUT#self_assign', 'admin_statusgruppe.php', '', '', '', 1405689852, 0);
